import React, { Component , useEffect, useState} from 'react'
import {Link} from 'react-router-dom';
import axios from 'axios';

export default class Map extends Component {
    constructor(props) {
        super(props);
        this.onChangeDepart = this.onChangeDepart.bind(this);

        this.onChangeArrive = this.onChangeArrive.bind(this);
        this.state = {
            depart: '',
            arrive: '',
            post: {}            
        }
    }

    onChangeDepart(e) {

        this.setState({
          depart: e.target.value
        })
      }
      onChangeArrive(e) {
        this.setState({
          arrive: e.target.value
        })
    
      }
    componentDidMount(){
        fetch('https://restcountries.eu/rest/v2/all')
          .then ((response)=> {
            return response.json()
        })
          .then((result) => {          
                this.setState({post : result})     
          })
    }
    // eslint-disable-next-line no-useless-constructor
    

     search = async(e) => {

        if (e.key == 'Enter') {

            const data = await fetch(this.state.depart)
            const data1 = await fetch(this.state.arrive)

            this.setState(data);
            this.setState(data1);
            const latitude = this.data.latlng[0];
            const Longitude = this.data.latlng[1];

        }
     }

    render(){
        return(
            <div className="container-fluid form-group d-md-flex justify-content-md-center" data-aos="fade" data-aos-duration="1000" >
                <input className="bg-light border rounded shadow form-control-lg d-flex col-sm" type="text" placeholder="Départ" required="" autofocus="" autocomplete="on" name="départ" value={this.state.depart} onChange = {this.onChangeDepart}/>
                <input className="bg-light border rounded shadow form-control-lg d-flex col-sm" type="text" placeholder="Destination"  autoFocus="" autoComplete="on" required="" name="destination" value={this.state.arrive} onChange = {this.onChangeArrive}/>
                <input className="bg-light border rounded shadow form-control-lg d-flex col-sm" type="date"/> 
                <p>{this.latitude}</p>
                
                <br/>    
            </div>  
            
        )
    }
}
