import React, { Component , useEffect, useState} from 'react'
import {Link} from 'react-router-dom';
import axios from 'axios';

export default class Map extends Component {
    constructor(props) {
        super(props);
        this.state = {
            depart: {},
            arrive: {}
        }
    }

    componentDidMount(){
        fetch('https://restcountries.eu/rest/v2/all')
          .then ((response)=> {
            return response.json()
        })
          .then((result) => {          
                this.setState({post : result})     
          })
    }
    // eslint-disable-next-line no-useless-constructor
    

    render(){
        return(
            <div className="container-fluid form-group d-md-flex justify-content-md-center" data-aos="fade" data-aos-duration="1000" >
                <input className="bg-light border rounded shadow form-control-lg d-flex col-sm" type="text" placeholder="Départ" required="" autoFocus="" autoComplete="on" name="départ" value={this.state.depart}/>
                <input className="bg-light border rounded shadow form-control-lg d-flex col-sm" type="text" placeholder="Destination"  autoFocus="" autoComplete="on" required="" name="destination" value={this.state.arrive}/>
                <input className="bg-light border rounded shadow form-control-lg d-flex col-sm" type="date"/> 
                {(this.state.depart) ? this.state.depart:<p>Chargement ...</p>}
                
                <br/>    
            </div>  
            
        )
    }
}
